function getIndex(t){const e=document.querySelector(".header__sidebar-item"),i=Array.from(e.children);if(t.matches(".header__sidebar-item button")){return i.indexOf(t.closest(".sidebar-menu"))}}document.addEventListener("alpine:init",(()=>{Alpine.data("sidebar",(()=>({sidebarWidth:100,position:"translate-x-0",moveLeft(t){currentIndex=parseInt(getIndex(t)+1),widthToMove=String(currentIndex*this.sidebarWidth),"0"===widthToMove?this.position="translate-x-0":this.position="-translate-x-"+widthToMove,console.log(this.position)},moveRight(t){currentIndex=parseInt(getIndex(t)-1),widthToMove=String(currentIndex*this.sidebarWidth),"0"===widthToMove?this.position="translate-x-0":this.position="-translate-x-"+widthToMove,console.log(this.position)}})))}));